%script 
--Drop any previous models for Notebook repeatability

DECLARE
v_sql varchar2(100);

BEGIN
    v_sql := 'CALL DBMS_DATA_MINING.DROP_MODEL(''MODEL_NAME_AR'')';
    EXECUTE IMMEDIATE v_sql;
    DBMS_OUTPUT.PUT_LINE (v_sql ||': succeeded');
EXCEPTION
    WHEN OTHERS THEN
    DBMS_OUTPUT.PUT_LINE (v_sql ||': drop unneccessary - no model exists');
END;
/

%script

DECLARE
v_sql varchar2(100);

--drop build settings 
BEGIN
    v_sql := 'DROP TABLE SETTINGS_TABLE_NAME_AR';
    EXECUTE IMMEDIATE v_sql;
    DBMS_OUTPUT.PUT_LINE (v_sql ||': succeeded');
EXCEPTION
    WHEN OTHERS THEN
    DBMS_OUTPUT.PUT_LINE (v_sql ||': drop unneccessary - no table exists');
END;
/

%script 

DECLARE
v_sql varchar2(100);

--Create a Build Setting for DT Model Build 
BEGIN
    v_sql := 'CREATE TABLE SETTINGS_TABLE_NAME_AR (SETTING_NAME  VARCHAR2(30),SETTING_VALUE VARCHAR2(4000))';
    EXECUTE IMMEDIATE v_sql;
    DBMS_OUTPUT.PUT_LINE (v_sql ||': succeeded');
EXCEPTION
    WHEN OTHERS THEN
    DBMS_OUTPUT.PUT_LINE (v_sql ||': create failed');
END;
/

